import React from 'react';
import logo from './logo.svg';
import './App.css';
import io,{socketIOClient,openSocket} from 'socket.io-client';
const sock= io("http://localhost:4000/tech")
class App extends React.Component {

  constructor(props){
    super(props)
    this.state={
      data:null,
      socket:io("http://localhost:4000/tech")
    }

  }
  componentDidMount(){


   const room="java"

   const res=sock.emit('join',{room})
   this.setState({data:res})
  }
  handleSumbit=(e)=>{
    let data={msg:"Hi",room:"java"}
    this.statesock.emit('message',data)
  }
  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            {console.log(this.state.data)}

          </a>
        </header>
        <button type="sumbit" onClick={this.handleSumbit}>Send</button>
      </div>
    );
  }

}

export default App;
