import React from 'react'
const MessageList=({messageList})=>(
    
        messageList.map(ms=><div>{ms.message}</div>)
  

)
 class  MessageBox extends React.Component{
     constructor(props){
         super(props)
         this.state={
             messageList:[]
         }
     }
     componentWillReceiveProps(newProps){
         console.log(newProps)
         let messages=this.state.messageList
         messages.push(newProps)
         
         this.setState({messageList:messages})
     }
     messages=this.state.messageList.map((ms,i)=>{
        return (<MessageList key={i} message={ms}/>)}
        ).bind(this)
     render(){

             if(this.state.messageList.length===0) return(<div>Loading Messages</div>)
             else  return (
             <div>
                { messages()}
            </div>)
     }
 }

export default MessageBox;
