import React from 'react';
import logo from './logo.svg';
import './App.css';
import io,{socketIOClient,openSocket} from 'socket.io-client';
import MessageBox from './MessageBox'
class App extends React.Component {

  constructor(props){
    super(props)
    this.state={
      data:null,
      socket:io("http://localhost:5000/tech"),
      room:"java",
      messages:{}
    }
   /* const {socket}=this.state
    socket.on('message',(data)=>{
      console.log(data)
      this.setState({data})
    })*/


  }

  componentDidMount(){
    const {socket,room}=this.state
    socket.emit('join',{room})
    socket.on('message',(msg)=>{
      console.log(msg)
      let messageArray=this.state.messages
      messageArray.
      messageArray.push({msg});

      this.setState({messages:messageArray})
    })




  }
  handleSumbit=(e)=>{
    let data={msg:"Hi",room:"java"}
    this.state.socket.emit('message',data)
  }
  render(){
    return (
      <div className="App">
        <h5>{this.state.room}</h5>
        <MessageBox messages={this.state.messages}/>
        <button type="sumbit" onClick={this.handleSumbit}>Send</button>
      </div>
    );
  }

}

export default App;
